const e=document.querySelector(".custom-cursor"),t=document.querySelector(".cursor-dot"),s=document.querySelectorAll("a, button");let o=!1,c=0,r=0,u=0,n=0;const l=()=>{e.classList.add("custom-cursor--init"),o=!0};s.forEach(t=>{t.addEventListener("mouseover",()=>{e.classList.add("custom-cursor--link")}),t.addEventListener("mouseout",()=>{e.classList.remove("custom-cursor--link")})});const m=()=>{u+=(c-u)*.05,n+=(r-n)*.05,e.style.left=`${u}px`,e.style.top=`${n}px`,t.style.left=`${c}px`,t.style.top=`${r}px`,requestAnimationFrame(m)};document.addEventListener("mousemove",e=>{c=e.clientX,r=e.clientY,o||l()}),document.addEventListener("mouseout",()=>{e.classList.remove("custom-cursor--init"),o=!1}),m();