document.addEventListener("DOMContentLoaded",function(){document.body.classList.add("loaded"),document.querySelectorAll("a:not([target='_blank'])").forEach(t=>{t.addEventListener("click",function(t){let e=this.href.startsWith(window.location.origin),n=!this.href.includes("#");e&&n&&(t.preventDefault(),document.body.classList.add("fade-out"),setTimeout(()=>{window.location.href=this.href},500))})});let t=document.getElementById("back-btn");t&&t.addEventListener("click",function(t){t.preventDefault(),document.body.classList.add("fade-out"),setTimeout(()=>{history.back()},500)})});